  const [chord, setChords]=useState('A major');
  const [flag, setFlag]=useState(0);
  const myArray = [
    { id: 1, name: 'A Major' },
    { id: 2, name: 'B Major' },
    { id: 3, name: 'C Major' },
    { id: 4, name: 'D Major' },
    { id: 5, name: 'E Major' },
    { id: 6, name: 'F Major' },
    { id: 7, name: 'G Major' },
  ];
  useEffect(()=>{
    const interval =setInterval(()=>{
      setSeconds(seconds=>seconds+1);

    },1000);
    return () =>clearInterval(interval);
  },[]);

  var randomItem = myArray[Math.floor(Math.random()*myArray.length)];
  return (
    <>
      <style type="text/css">
    {`
    .btn-flat {
      background-color: #f7cee0;
      color: white;
    }

    .btn-xxl {
      padding: 1rem 1.5rem;
      font-size: 1.5rem;
    }
    `}
  </style>
       <h1 ><span style={{ color: '#a5ceec' }}>HELLO</span> <span style={{ color: '#f7cee0' }}>WELCOME</span> <span style={{ color: '#f7cee0' }}>TO</span><span style={{ color: '#f7cee0' }}> AIR MUSIC</span></h1>
       <Button variant="flat" size="xxl"
       onClick={()=>{
         setFlag(!flag)
         }}>
        {flag? 'READY TO FLY': 'READY TO LAND'}
  </Button>
  <h1 style={{ color: '#f7cee0' , fontSize: 100}}
  >

  {randomItem.name}
  </h1>

     </>
  );
}

export default Down;
