import part from './part.svg';
import Button from 'react-bootstrap/Button';
import React, {useState, useEffect} from 'react';
import A from './A.mp3'
import B from './A.mp3'
import C from './A.mp3'
import D from './A.mp3'
import E from './A.mp3'
const Down= () => {
  const [chord, setChords]=useState('A major');
  const [flag, setFlag]=useState(0);
  let audio = new Audio(A)
  const start = () => {
    audio.play()
  }
  const myArray = [
    { id: 1, name: 'A' },
    { id: 2, name: 'B' },
    { id: 3, name: 'C' },
    { id: 4, name: 'D' },
    { id: 5, name: 'E' },
    { id: 6, name: 'F' },
    { id: 7, name: 'G' },
  ];
  useEffect(()=>{
    const interval =setInterval(()=>{
      setChords(chord=>myArray[Math.floor(Math.random()*myArray.length)]);

    },1000);
    return () =>clearInterval(interval);
  },[]);
  return (
    <div style={{ backgroundImage: "url(./part.svg)" }}>
      <style type="text/css">
    {`
    .btn-flat {
      background-color: #f7cee0;
      color: white;
    }

    .btn-xxl {
      padding: 1rem 1.5rem;
      font-size: 1.5rem;
    }
    `}
  </style>
       <h1 ><span style={{ color: '#a5ceec' }}>HELLO</span> <span style={{ color: '#f7cee0' }}>WELCOME</span> <span style={{ color: '#f7cee0' }}>TO</span><span style={{ color: '#f7cee0' }}> AIR MUSIC</span></h1>
       <Button variant="flat" size="xxl"
       onClick={()=>{
         setFlag(!flag)
         start();
         }}>
        {flag? 'READY TO FLY': 'READY TO LAND'}
  </Button>
  <h1 style={{ color: '#f7cee0' , fontSize: 100}}
  >

{flag? 'wohoooo!': chord.name}
  </h1>

     </div>
  );
}

export default Down;
